    private void srchbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_srchbtnActionPerformed
        Search m;
        try {
            m = new Search();
            Product pr = new Product();
            pr.setName(srchname.getText().toLowerCase());
            pr.setCategory(srchCategory.getText().toLowerCase());
            pr.setProductionDate(srchProDate.getText());
            pr.setExpiringDate(srchExpDate.getText());
            ArrayList<Product> srchProducts = new ArrayList<>();
            srchProducts = m.searchName(pr);

            DefaultTableModel model = (DefaultTableModel) searchTable.getModel();

            model.setRowCount(0);
            for (Product p : srchProducts) {
                Object[] row = {
                    p.getID(), // Index 0: ID
                    p.getName(), // Index 1: Name
                    p.getCategory(), // Index 2: Category
                    p.getPrice(), // Index 3: Price
                    p.getQuantity(), // Index 4: Quantity
                    p.getProductionDate(), // Index 5: ProdDate
                    p.getExpiringDate() // Index 6: ExpDate
                };
                model.addRow(row);
                
            }

            DefaultTableCellRenderer centerRenderer = new DefaultTableCellRenderer();
            centerRenderer.setHorizontalAlignment(SwingConstants.CENTER);

            // Apply the renderer to each column
            for (int i = 0; i < searchTable.getColumnCount(); i++) {
                searchTable.getColumnModel().getColumn(i).setCellRenderer(centerRenderer);
            }
        } catch (FileNotFoundException ex) {
            System.out.println("not found");
        }


    }//GEN-LAST:event_srchbtnActionPerformed

    private void deletebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deletebtnActionPerformed
        // TODO add your handling code here:
        
        int selectedRow = searchTable.getSelectedRow();
        DefaultTableModel mo = (DefaultTableModel) searchTable.getModel();
        int specificColumn = 0; // Specify the column index (e.g., column 1 for "Age")
        
        if (selectedRow != -1) { // Check if a row is selected
            Object cellData = searchTable.getValueAt(selectedRow, specificColumn);
            int id = (Integer) cellData;
            try {
                new Modification().removeProduct(id);
            } catch (FileNotFoundException ex) {
                System.out.println(ex);
            }
            mo.removeRow(selectedRow);
            System.out.println("Cell Data: " + id);
        } else {
            System.out.println("No row selected!");
        }
        
       
    }//GEN-LAST:event_deletebtnActionPerformed

