    private void addingbtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_addingbtnMouseClicked
        
        try {
            int flag = mod.addProduct(nameTxtField.getText().toLowerCase(), categoryTxt.getText().toLowerCase(), Integer.valueOf(priceTxt.getText()), Integer.valueOf(quantityTxt.getText()), dateTxt.getText(), ExpDateTxt.getText() , SupplierFiled.getText() , Integer.valueOf(BoughtPriceFiled.getText()));
            if (flag == 1) {
                validationError.setText("  Added Successfully.");
                validationError.setForeground(Color.green);

            }else if(flag == -2){
                System.out.println("Error: Invalid date.");
                validationError.setText("  Error: Invalid date");
                validationError.setForeground(Color.red);
            }
            else if(nameTxtField.getText().length()==0 || categoryTxt.getText().length() == 0){
                validationError.setText("Error:Incomplete details.");
                validationError.setForeground(Color.red);
            }

            else {
                validationError.setText("Error: Repeated Product.");
                validationError.setForeground(Color.red);

            }
        } catch (Exception ex) {
            System.out.println("Error: Incomplete details.");
            validationError.setText("Error: Incomplete details.");
            validationError.setForeground(Color.red);
        }

    }//GEN-LAST:event_addingbtnMouseClicked

